import React, { useEffect, useRef } from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import CountUp from "react-countup";
import "swiper/css";

const AnimatedNumber = ({ value, suffix }) => {
  const elementRef = useRef(null);

  useEffect(() => {
    const countUp = new CountUp(elementRef.current, value);
    if (!countUp.error) {
      countUp.start();
    } else {
      console.error(countUp.error);
    }
  }, [value]);

  return <span ref={elementRef}>{value}{suffix}</span>;
};

const Slider = () => {
  return (
    <div>
      <div className="container mt-4">
        <div className="title" style={{ maxWidth: 450 }}>
          <h2>Know what your healthcare is up against.</h2>
          <div className="des">
            <p className="text-center m-auto">
              Here’s a preview of some of the new cybersecurity data uncovered
              by this year’s DBIR.
            </p>
          </div>
        </div>
        <div className="row">
          <div className="col-md-4">
            <div className="card mb-4">
              <div className="card-body">
                <AnimatedNumber value={83} suffix="%" />
                <p className="card-text">
                  of breaches involved external actors—with the majority being
                  financially motivated.
                </p>
              </div>
            </div>
          </div>

          <div className="col-md-4">
            <div className="card mb-4">
              <div className="card-body">
                <AnimatedNumber value={74} suffix="%" />
                <p className="card-text">
                  of breaches involved the human element, which includes social
                  engineering attacks, errors or misuse.
                </p>
              </div>
            </div>
          </div>

          <div className="col-md-4">
            <div className="card mb-4">
              <div className="card-body">
                <AnimatedNumber value={50} suffix="%" />
                <p className="card-text">
                  of all social engineering attacks are pretexting
                  incidents—nearly double last year’s total.
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Slider;
